// IMG
import person4 from "../../img/person4.jpg";
import person5 from "../../img/person5.jpg";
import person6 from "../../img/person6.jpg";
import person7 from "../../img/person7.jpg";

import React from "react";
import css from "./User.module.css";

const User = (props) => {
  if (props.users.length === 0) {
    props.setUsers([
      {
        id: 1,
        img: person4,
        followed: false,
        fullName: "Jemima Mora",
        location: { country: "Ukraine", city: "Kiev" },
        status: "Prefer SUNDAY!",
      },
      {
        id: 2,
        img: person5,
        followed: true,
        fullName: "Justine Floyd",
        location: { country: "Belarus", city: "Minsk" },
        status: "Call me Gary the Crummy :)",
      },
      {
        id: 3,
        img: person6,
        followed: false,
        fullName: "Honey Bannister",
        location: { country: "Russia", city: "Moscow" },
        status: "A day without laughter is a day wasted",
      },
      {
        id: 4,
        img: person7,
        followed: true,
        fullName: "Ellise Lacey",
        location: { country: "Germany", city: "Berlin" },
        status: "Laughter is America's most important export!",
      },
    ]);
  }

  let userFollowed = props.userFollowed;
  return (
    <div className={css.form} key={props.users.id}>
      <div className={css.content}>
        <div className={css.person}>
          <img src={props.users.img} />
        </div>
        <div className={css.data}>
          <div className={css.fullName}>{props.users.fullName}</div>
          <div className={css.inData}>
            <div className={css.location}>
              {props.country} - {props.users.location.city}
            </div>
            <div className={css.status}>"{props.users.status}"</div>
          </div>
        </div>
        <div className={css.button}>
          {userFollowed ? (
            <button
              className={css.followBn}
              onClick={() => {
                props.follow(props.users.id);
              }}
            >
              <text>FOLLOW</text>
            </button>
          ) : (
            <button
              className={css.unfollowBn}
              onClick={() => {
                props.unfollow(props.users.id);
              }}
            >
              <text>UNFOLLOW</text>
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default User;
