import * as axios from "axios";

export const getUsers = (currentPage = 1, pageSize = 4) => {
  return axios
    .get(
      `https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pageSize}`,
      { withCredentials: true }
    )
    .this((response) => response.data);
};
