import React from "react";
import BG from "./FriendsBG/FriendsBG";
import css from "./Friends.module.css";
// IMG
import person4 from "../../img/person4.jpg";
import person5 from "../../img/person5.jpg";
import person6 from "../../img/person6.jpg";
import person7 from "../../img/person7.jpg";
import Axios from "axios";

const Friends = (props) => {
  if (props.users.length === 0) {
    Axios.get("https://social-network.samuraijs.com/docs/users")
    props.setUsers([
      {
        id: 1,
        img: person4,
        followed: false,
        fullName: "Jemima Mora",
        location: { country: "Ukraine", city: "Kiev" },
        status: "Prefer SUNDAY!",
      },
      {
        id: 2,
        img: person5,
        followed: true,
        fullName: "Justine Floyd",
        location: { country: "Belarus", city: "Minsk" },
        status: "Call me Gary the Crummy :)",
      },
      {
        id: 3,
        img: person6,
        followed: false,
        fullName: "Honey Bannister",
        location: { country: "Russia", city: "Moscow" },
        status: "A day without laughter is a day wasted",
      },
      {
        id: 4,
        img: person7,
        followed: true,
        fullName: "Ellise Lacey",
        location: { country: "Germany", city: "Berlin" },
        status: "Laughter is America's most important export!",
      },
    ]);
  }

  let userForm = props.users.map((user) => (
    <div className={css.form} key={user.id}>
      <div className={css.content}>
        <div className={css.person}>
          <img src={user.img} />
        </div>
        <div className={css.data}>
          <div className={css.fullName}>{user.fullName}</div>
          <div className={css.inData}>
            <div className={css.location}>
              {user.location.country} - {user.location.city}
            </div>
            <div className={css.status}>"{user.status}"</div>
          </div>
        </div>
        <div className={css.button}>
          {user.followed ? (
            <button
              className={css.followBn}
              onClick={() => {
                props.unfollow(user.id);
              }}
            >
              <text>FOLLOW</text>
            </button>
          ) : (
            <button
              className={css.unfollowBn}
              onClick={() => {
                props.follow(user.id);
              }}
            >
              <text>UNFOLLOW</text>
            </button>
          )}
        </div>
      </div>
    </div>
  ));

  return (
    <div className={css.friendsPage}>
      <div className={css.users}>
        <div className={css.listName}>
          <h3>Your list of users</h3>
        </div>
        {userForm}
      </div>
      <BG />
    </div>
  );
};

export default Friends;
