import React, { Component, useState } from "react";
import css from "./ProfileStatus.module.css";

const textInput = React.createRef();

function ProfileStatus(props) {
  // HOOKS
  const [editorMode, setEditorMode] = useState(false);
  const [status, setStatus] = useState(props.status);
  useEffect(() => {
    effect
    return () => {
      cleanup
    }
  }, [input])
  // const [count, setCount] = useState(0);
  // const setCountFromInput = () => {
  //   setCount(textInput.current.value);
  // };
  const activateEditorMode = () => {
    setEditorMode(true);
  };
  const deactivateEditorMode = () => {
    setEditorMode(false);
    props.updateStatus(status);
  };
  const onStatusChange = (e) => {
    setStatus(e.currentTarget.value);
  };
  return (
    <div>
      {editorMode ? (
        <input
          onChange={onStatusChange}
          autoFocus={true}
          onBlur={deactivateEditorMode}
          value={status}
          className={css.statusChange}
        />
      ) : (
        <div onDoubleClick={activateEditorMode} className={css.statusRead}>
          {props.status || "No status"}
        </div>
      )}
      {/* <div className="">
        <input type="text" ref={textInput} />
        <button onClick={setCountFromInput}>Set</button>
      </div>
      <button onClick={() => setCount(count - 1)}>-</button>
      <button onClick={() => setCount(count + 1)}>+</button>
      <span>{count}</span> */}
    </div>
  );
}

export default ProfileStatus;
