import { connect } from "react-redux";
import { addMessageActionCreator } from "../../redux/myReducers/messages-reducer";
import {
  getDialogs,
  getMessages,
} from "../../redux/selectors/messages-selector";
import React, { Component } from "react";
import css from "./Messages.module.css";
import DialogItem from "./DialogItem/DialogItem";
import MessagesBG from "./MessagesBG/MessagesBG";
import Chat from "./Chat/Chat";

class Messages extends Component {
  render() {
    const dialogsElement = this.props.dialogs.map((dialog) => (
      <DialogItem name={dialog.name} id={dialog.id} />
    ));
    return (
      <div className={css.messages}>
        <MessagesBG />
        <div className={css.dialogs}>
          <div className={css.dialogsItems}>{this.dialogsElement}</div>
          <div className={css.line} />
          <Chat
            messages={props.messages}
            sentMessage={this.props.sentMessage}
          />
        </div>
      </div>
    );
  }
}

let mapStateToProps = (state) => {
  return {
    dialogs: getDialogs(state),
    messages: getMessages(state),
  };
};

let mapDispatchToProps = (dispatch) => {
  return {
    // DISPATCH ADD MESSAGE ACTION CREATOR
    sentMessage: (newMessage) => {
      dispatch(addMessageActionCreator(newMessage));
    },
  };
};

// REDIRECT AND CONNECT
export default connect(mapStateToProps, mapDispatchToProps)(Messages);
