import React from "react";
import css from "./User.module.css";

const User = (props) => {
  let userFollowed = props.userFollowed;
  return (
    <div className={css.form} key={props.id}>
      <div className={css.content}>
        <div className={css.person}>
          <img src={props.img} />
        </div>
        <div className={css.data}>
          <div className={css.fullName}>{props.fullName}</div>
          <div className={css.inData}>
            <div className={css.location}>
              {props.location.country} - {props.location.city}
            </div>
            <div className={css.status}>"{props.status}"</div>
          </div>
        </div>
        <div className={css.button}>
          {userFollowed ? (
            <button
              className={css.followBn}
              onClick={() => {
                props.unfollow(user.id);
              }}
            >
              <text>FOLLOW</text>
            </button>
          ) : (
            <button
              className={css.unfollowBn}
              onClick={() => {
                props.unfollow(user.id);
              }}
            >
              <text>UNFOLLOW</text>
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default User;
