import React from "react";
import css from "./Chat.module.css";
import Message from "./Message/Message";
import MessagesFormContainer from "./MessagesForm/MessagesFormContainer";

const Chat = (props) => {
  let messagesElement = props.messages.map((message) => (
    <Message message={message.message} />
  ));
  // ONCLICK FOR TEXTAREA
  let onSentMessage = (values) => {
    props.sentMessage(values.newMessage);
  };
  return (
    <div className={css.chat}>
      <div className={css.messages}>
        <div>{messagesElement}</div>
      </div>
      <MessagesFormContainer onSubmit={onSentMessage} />
    </div>
  );
};

export default Chat;
