import React, { Component } from "react";
import css from "./Messages.module.css";
import { NavLink } from "react-router-dom";
 
const Message = (props) => {
  return <div className={css.dialog}>{props.message}</div>;
};

const DialogItem = (props) => {
  let path = "/messages/" + props.id;

  return (
    <div className={css.dialog + " " + css.active}>
      <NavLink to={path} className={css.name} activeClassName={css.activeLink}>
        {props.name}
      </NavLink>
    </div>
  );
};

export default class Messages extends Component {
  render(props) {
    let dialogsElement = props.dialog.map((dialog) => (
      <DialogItem name={dialog.name} id={dialog.id} />
    ));

    let messagesElement = props.message.map((message) => (
      <Message message={message.message} />
    ));

    return (
      <div className={css.dialogs}>
        <div className={css.dialogsItems}>{dialogsElement} </div>
        <div className={css.line}></div>
        <div className={css.messages}>{messagesElement} </div>
      </div>
    );
  }
}
