import React from "react";
import ReduxMessagesForm from "../../Messages/Chat/MessagesForm/MessagesForm";
import PostContainer from "./Post/PostContainer";
import css from "./Posts.module.css";
import ProfileBG from "./ProfileBG/ProfileBG";

const Posts = (props) => {
  // ONCLICK FOR TEXTAREA
  let onSentPost = (value) => {
    props.sentPost(value.newPost);
  };
  return (
    <div className={css.posts}>
      <ProfileBG />
      <ReduxMessagesForm onSubmit={onSentPost} />
      <PostContainer />
    </div>
  );
};

export default Posts;
