import React, { Component } from "react";
import css from "./ProfileStatus.module.css";

export default class ProfileStatus extends Component {
  state = {
    editorMode: false,
  };
  activeEditorMode() {
    this.setState({
      editorMode: true,
    });
  }
  render() {
    return (
      <div onDoubleClick={this.activeEditorMode.bind(this)}>
        {this.state.editorMode ? (
          <input value={this.props.status} />
        ) : (
          <div className={css.aboutMe}>{this.props.status}</div>
        )}
      </div>
    );
  }
}
