import React, { Component } from "react";
import Item from "./First/First";
import css from "./Post.module.css";
import person1 from "../../../../photos/person1.jpg";
import person3 from "../../../../photos/person3.jpg";
import First from "./First/First";
import Second from "./Second/Second";

export default class Post extends Component {
  render() {
    let postData = [
      {
        id: 1,
        message: "Hi, checkout my new photo!",
        name: "Maria Simpson ",
        img: person1,
      },
      {
        id: 2,
        message: "It was a beautiful movie :)",
        name: "Alex Dochkon",
        img: person3,
      },
    ];
    return (
      <div className={css.posts}>
        <div className={css.first}>
          <First
            message={postData[0].message}
            name={postData[0].name}
            img={postData[0].img}
          />
        </div>
        <div className={css.second}>
          <Second
            message={postData[1].message}
            name={postData[1].name}
            img={postData[1].img}
          />
        </div>
      </div>
    );
  }
}
