import React from "react";
import BG from "./FriendsBG/FriendsBG";
import css from "./Friends.module.css";
// IMG
import person4 from "../../img/person4.jpg";
import person5 from "../../img/person5.jpg";
import person6 from "../../img/person6.jpg";
import person7 from "../../img/person7.jpg";

const Friends = (props) => {
  if (props.users.length === 0) {
    props.setUsers([
      {
        id: 1,
        img: person4,
        followed: false,
        fullName: "Jemima Mora",
        location: { country: "Ukraine", city: "Kiev" },
        status: "Prefer SUNDAY!",
      },
      {
        id: 2,
        img: person5,
        followed: true,
        fullName: "Justine Floyd",
        location: { country: "Belarus", city: "Minsk" },
        status: "Call me Gary the Crummy :)",
      },
      {
        id: 3,
        img: person6,
        followed: false,
        fullName: "Honey Bannister",
        location: { country: "Russia", city: "Moscow" },
        status: "A day without laughter is a day wasted",
      },
      {
        id: 4,
        img: person7,
        followed: true,
        fullName: "Ellise Lacey",
        location: { country: "Germany", city: "Berlin" },
        status: "Laughter is America's most important export!",
      },
    ]);
  }

  let userForm = props.users.map((u) => (
    <div key={u.id}>
      <span>
        <div>
          <img src={u.photoUrl} className={css.userPhoto} />
        </div>
        <div>
          {u.followed ? (
            <button
              onClick={() => {
                props.unfollow(u.id);
              }}
            >
              Unfollow
            </button>
          ) : (
            <button
              onClick={() => {
                props.follow(u.id);
              }}
            >
              Follow
            </button>
          )}
        </div>
      </span>
      <span>
        <span>
          <div>{u.fullName}</div>
          <div>{u.status}</div>
        </span>
        <span>
          <div>{u.location.country}</div>
          <div>{u.location.city}</div>
        </span>
      </span>
    </div>
  ));

  return (
    <div className={css.friendsPage}>
      <div className={css.users}>
        <div className={css.listName}>
          <h3>Your list of users</h3>
        </div>
        {userForm}
      </div>
      <BG />
    </div>
  );
};

export default Friends;
