import React from "react";
import css from "./ChatTextarea.module.css";

let addMessageActionCreator = () => {
  return  {type: "ADD-MESSAGE"}
}
let updateNewMessageTextActionCreator = (text) => {
  return  {type: "UPDATE-NEW-MESSAGE-TEXT", newMessageText: text}
}

const ChatTextarea = (props) => {
  let newMessage = React.createRef();

  let sentMessage = () => {
    props.dispatch(addMessageActionCreator());
  };

  let onMessageChange = () => {
    let text = newMessage.current.value;
    props.dispatch(updateNewMessageTextActionCreator(text));
  };

  return (
    <div className={css.area}>
      <textarea
        ref={newMessage}
        className={css.textarea}
        onChange={onMessageChange}
        value={props.newMessageText}
      ></textarea>
      <button className={css.send} onClick={sentMessage}>
        SEND
      </button>
    </div>
  );
};

export default ChatTextarea;
