// TYPE FOR MESSAGES
const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS = "SET-USERS";
// INITIAL STATE
let initialState = {
  // all users
  users: [],
  // number of portion of items
  page: 4,
  // page size (how many items will be returned in response)
  count: 21,
};

// THIS REDUCER TAKES IN THE STATE AND THE ACTION CALLED
const friendsReduser = (state = initialState, action) => {
  switch (action.type) {
    //   FOLLOW
    case FOLLOW: {
      return {
        ...state,
        // users: [...state.users]
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            return { ...user, followed: true };
          }
          return user;
        }),
      };
    }
    // UNFOLLOW
    case UNFOLLOW: {
      return {
        ...state,
        // users: [...state.users]
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            return { ...user, followed: false };
          }
          return user;
        }),
      };
    }
    // SET USERS
    case SET_USERS: {
      return { ...state, users: [...state.users, ...action.users] };
    }
    default:
      return state;
  }
};

// FOLLOW ACTION CREACTOR
export const followActionCreator = (userId) => ({ type: FOLLOW, userId });
// UNFOLLOW ACTION CREACTOR
export const unfollowActionCreator = (userId) => ({
  type: UNFOLLOW,
  userId,
});
// SET USERS ACTION CREACTOR
export const setUsersActionCreator = (users) => ({
  type: SET_USERS,
  users,
});

export default friendsReduser;
