import React from "react";
import css from "./Users.module.css";
import UserContainer from "./UserContainer/User";

const Users = (props) => {
  let userForm = props.users.map((user) => (
    <div className={css.form} key={user.id}>
      <div className={css.content}>
        <div className={css.person}>
          <img src={user.img} />
        </div>
        <div className={css.data}>
          <div className={css.fullName}>{user.fullName}</div>
          <div className={css.inData}>
            <div className={css.location}>
              {props.country} - {user.location.city}
            </div>
            <div className={css.status}>"{user.status}"</div>
          </div>
        </div>
        <div className={css.button}>
          {userFollowed ? (
            <button
              className={css.followBn}
              onClick={() => {
                props.follow(user.id);
              }}
            >
              <text>FOLLOW</text>
            </button>
          ) : (
            <button
              className={css.unfollowBn}
              onClick={() => {
                props.unfollow(user.id);
              }}
            >
              <text>UNFOLLOW</text>
            </button>
          )}
        </div>
      </div>
    </div>
  ));
  return (
    <div className={css.users}>
      <div className={css.listName}>
        <h3>Your list of users</h3>
      </div>
      {userForm}
    </div>
  );
};

export default Users;
