import React from "react";
import ReduxLoginForm from "./LoginForm/LoginForm";
import css from "./Login.module.css";
import { connect } from "react-redux";
import {
  loginThunkCreator,
  logoutThunkCreator,
} from "../../redux/myRedusers/auth-reduser";

const Login = (props) => {
  const onSubmit = (formData) => {
    props.loginThunkCreator(formData.email, formData.password, formData.recall);
  };
  return (
    <div>
      <h1 className={css.singInName}>Sing In</h1>
      <ReduxLoginForm onSubmit={onSubmit} {...props} />
    </div>
  );
};

export default connect(null, { loginThunkCreator, logoutThunkCreator })(Login);
