import { connect } from "react-redux";
import {
  followActionCreator,
  setUsersActionCreator,
  unfollowActionCreator,
  setCurrentPageActionCreator,
  setTotalUsersCountActionCreator,
} from "../../redux/myRedusers/friends-reduser";
import React, { Component } from "react";
import * as axios from "axios";
import Friends from "./Friends";

class FriendsAPIComponent extends Component {
  componentDidMount() {
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${this.currentPage}&count=${this.pageSize}`
      )
      .then((response) => {
        this.setUsers(response.data.items);
        this.setTotalUsersCount(response.data.totalCount);
      });
  }

  setPage = (pageNumber) => {
    this.setCurrentPage(pageNumber);
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.pageSize}`
      )
      .then((response) => {
        this.setUsers(response.data.items);
      });
  };

  render() {
    return (
      <Friends
        totalUsersCount={this.totalUsersCount}
        pageSize={this.pageSize}
        users={this.users}
        unfollow={this.unfollow}
        currentPage={this.currentPage}
        follow={this.follow}
        setPage={this.setPage}
      />
    );
  }
}

let mapStateToProps = (state) => {
  return {
    users: state.friendsPage.users,
    pageSize: state.friendsPage.pageSize,
    totalUsersCount: state.friendsPage.totalUsersCount,
    currentPage: state.friendsPage.currentPage,
  };
};

let mapDispatchToProps = (dispatch) => {
  return {
    follow: (userId) => {
      dispatch(followActionCreator(userId));
    },
    unfollow: (userId) => {
      dispatch(unfollowActionCreator(userId));
    },
    setUsers: (users) => {
      dispatch(setUsersActionCreator(users));
    },
    setCurrentPage: (pageNumber) => {
      dispatch(setCurrentPageActionCreator(pageNumber));
    },
    setTotalUsersCount: (totalUsersCount) => {
      dispatch(setTotalUsersCountActionCreator(totalUsersCount));
    },
  };
};
const FriendsContainer = connect(mapStateToProps, mapDispatchToProps)(FriendsAPIComponent);

export default FriendsContainer;
