import React, { Component } from "react";
import css from "./ProfileStatus.module.css";

export default class ProfileStatus extends Component {
  // GETTING REF FOR TEXTAREA
  newStatus = React.createRef();

  // LOCAL STATE
  state = {
    editorMode: false,
  };

  // EDITOR MODE FOR TEXTAREA
  activateEditorMode = () => {
    this.setState({
      editorMode: true,
      editorMode: true,
    });
  };

  // DEACTIVATE EDITOR MODE FOR TEXTAREA
  deactivateEditorMode = () => {
    this.setState({
      editorMode: false,
    });
    this.props.updateStatus(this.newStatus.current.value);
  };
  render() {
    return (
      <div>
        {this.state.editorMode ? (
          <input
            ref={this.newStatus}
            autoFocus={true}
            onBlur={this.deactivateEditorMode}
            value={this.props.status}
            className={css.statusChange}
          />
        ) : (
          <div
            onDoubleClick={this.activateEditorMode}
            className={css.statusRead}
          >
            {this.props.status}
          </div>
        )}
      </div>
    );
  }
}
