import React, { Component } from "react";
import css from "./ProfileStatus.module.css";

export default class ProfileStatus extends Component {
  state = {
    editorMode: false,
  };
  activateEditorMode() {
    this.setState({
      editorMode: true,
    });
  }
  deactivateEditorMode() {
    this.setState({
      editorMode: false,
    });
  }
  render() {
    return (
      <div
        autoFocus={true}
        onBlur={this.deactivateEditorMode.bind(this)}
        onDoubleClick={this.activateEditorMode.bind(this)}
      >
        {this.state.editorMode ? (
          <input value={this.props.status} className={css.statusChange} />
        ) : (
          <div className={css.statusRead}>{this.props.status}</div>
        )}
      </div>
    );
  }
}
