import css from "./ProfileField.module.css";
export const ProfileField = ({
  input,
  label,
  type,
  meta: { touched, error, warning },
}) => (
  <div className={css.area}>
    <textarea className={css.textarea} {...input} {...meta} {...props} />
    <button className={css.send}>SEND</button>
  </div>
);
