import React, { Component } from "react";
import css from "./ChatTextarea.module.css";

export default class ChatTextarea extends Component {
  render() {
    return (
      <div className={css.area}>
        <textarea className={css.textarea}></textarea>
        <button className={css.send}>SEND</button>
      </div>
    );
  }
}
