import React from "react";
import css from "./ProfileField.module.css";

const ProfileField = ({ input, meta, ...props }) => {
  let hasError = meta.touched && meta.error;
  return (
    <>
      <div className={css.error}>{hasError && <span>{meta.error}</span>}</div>
      <div className={hasError ? css.areaError : css.area}>
        <textarea className={css.textarea} {...input} {...meta} {...props} />
        <button className={hasError ? css.sendError : css.send}>SEND</button>
      </div>
    </>
  );
};

export default ProfileField;
