function followUnfollowCase(state, functionalUserId, isFollowed) {
    return {
      ...state,
      // users: [...state.users]
      users: state.users.map((user) => {
        if (user.id === functionalUserId) {
          return { ...user, followed: isFollowed };
        }
        return user;
      }),
    };
  }
