import { connect } from "react-redux";
import {
  addMessageActionCreator,
  updateNewMessageTextActionCreator,
} from "../../../../redux/myRedusers/messages-reduser";
import ReduxMessagesForm from "./MessagesForm";


// LOGIC FOR CONTAINER
let mapStateToProps = (state) => {
  return {
    newMessageText: state.messagesPage.newMessageText,
  };
};

let mapDispatchToProps = (dispatch) => {
  return {
      // DISPATCH ADD MESSAGE ACTION CREATOR
    sentMessage: (newMessage) => {
      dispatch(addMessageActionCreator(newMessage));
    },
  };
};

// CONTAINER FOR CHAT TEXTAREA
const MessagesFormContainer = connect(mapStateToProps, mapDispatchToProps)(ReduxMessagesForm);

export default MessagesFormContainer;
