import React, { Component } from "react";
import css from "./ChatTextarea.module.css";

export default class ChatTextarea extends Component {
  render() {
    let newMessage = React.createRef();
    let sendMessage = () => {
      let text = newMessage.current.value;
      alert(text);
    };
    return (
      <div className={css.area}>
        <textarea className={css.textarea}></textarea>
        <button className={css.send} onClick={sendMessage}>
          SEND
        </button>
      </div>
    );
  }
}
