// TYPE FOR MESSAGES
const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS = "SET-USERS";
// IMG
 import person4 from "../../img/person4.jpg";

 let person4i = person4;
// INITIAL STATE
let initialState = {
  users: [
    {
      id: 1,
      img: person4i,
      followed: false,
      fullName: "Jemima Mora",
      location: { country: "Ukraine", city: "Kiev" },
      status: "Prefer SUNDAY!",
    },
    {
      id: 2,
      // img: person5,
      followed: true,
      fullName: "Justine Floyd",
      location: { country: "Belarus", city: "Minsk" },
      status: "Call me Gary the Crummy :)",
    },
    {
      id: 3,
      // img: person6,
      followed: false,
      fullName: "Honey Bannister",
      location: { country: "Russia", city: "Moscow" },
      status: "A day without laughter is a day wasted",
    },
  ],
};

// THIS REDUCER TAKES IN THE STATE AND THE ACTION CALLED
const friendsReduser = (state = initialState, action) => {
  switch (action.type) {
    //   FOLLOW
    case FOLLOW: {
      return {
        ...state,
        // users: [...state.users]
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            return { ...state.users, followed: true };
          }
          return user;
        }),
      };
    }
    // UNFOLLOW
    case UNFOLLOW: {
      return {
        ...state,
        // users: [...state.users]
        users: state.users.map((user) => {
          if (user.id === action.userId) {
            return { ...state.users, followed: false };
          }
          return user;
        }),
      };
    }
    // SET USERS
    case SET_USERS: {
      return { ...state, users: [...state.users, ...action.users] };
    }
    default:
      return state;
  }
};

// FOLLOW ACTION CREACTOR
export const followActionCreator = (userId) => ({ type: FOLLOW, userId });
// UNFOLLOW ACTION CREACTOR
export const unfollowActionCreator = (userId) => ({
  type: UNFOLLOW,
  userId,
});
// SET USERS ACTION CREACTOR
export const setUsersActionCreator = (users) => ({
  type: SET_USERS,
  users,
});

export default friendsReduser;
